import { Component, OnInit } from '@angular/core';
import { EmployeeService } from '../../services/employee.service';
import { Employee } from '../../interfaces/employee';
import { Location } from '@angular/common';
import { Router } from '@angular/router';
import { Page } from '../../interfaces/page';

@Component({
  selector: 'app-employees-list',
  templateUrl: './employees-list.component.html',
  styleUrls: ['./employees-list.component.scss']
})
export class EmployeesListComponent implements OnInit{

  // Initialize employeesListData with default values for its properties
  employeesListPageData: Page<Employee[]> = {
    content: [],
    pageable: {
      pageNumber: 0, // Set the default page number
      pageSize: 0,   // Set the default page size
      sort: null      // Set the default sorting
    },
    totalPages: 0,       // Set the default total pages
    totalElements: 0,    // Set the default total elements
    last: false,         // Set the default last
    size: 0,             // Set the default size
    number: 0,           // Set the default number
    sort: null,          // Set the default sorting
    numberOfElements: 0, // Set the default number of elements
    first: false,        // Set the default first
    empty: true          // Set the default empty
  };

  currentPage = 1;
  size = 5;

  constructor(private employeeService : EmployeeService,){

  }
  ngOnInit( ) {

    this.getEmployeesList();
  }

  
 

  getEmployeesList(){
    
    
    this.employeeService.getEmployeesList(this.currentPage - 1, this.size).subscribe((data) => {
      this.employeesListPageData = data;
      console.log('fetched employees', JSON.stringify(this.employeesListPageData));
    });


  }



  deleteEmployeeById(id : number){

    const fetchedId = Number(id);
    console.log('printing number '+ id);
    
    this.employeeService.deleteEmployeeById(fetchedId).subscribe((data) => {
      console.log('deleting ', data)
      console.log('employee has been deleted',id);
    });

    window.alert('Employee Has Been Delete Sucessfully employee :'+id);
    this.employeeService.redirectTo('/');

  }





  
}
